import 'reflect-metadata';
import { NestFactory } from '@nestjs/core';
import { AppModule } from './app.module';
import { ValidationPipe, Logger } from '@nestjs/common';
import { json, urlencoded } from 'express';

const logger = new Logger('Main');
let cachedApp: any;

export async function bootstrap() {
  if (cachedApp) return cachedApp;

  try {
    const app = await NestFactory.create(AppModule);

    // Increase payload limits for large Excel files and attachments
    app.use(json({ limit: '50mb' }));
    app.use(urlencoded({ extended: true, limit: '50mb' }));

    app.enableCors();

    const port = process.env.PORT ?? 5000;

    // Only call listen if not in serverless environment
    if (process.env.NODE_ENV !== 'production' || (process.env.VERCEL !== '1' && !process.env.NOW_REGION)) {
      await app.listen(port);
      logger.log(`Backend is running on: http://localhost:${port}`);
    }

    await app.init();
    cachedApp = app.getHttpAdapter().getInstance();
    return cachedApp;
  } catch (error) {
    logger.error('Failed to bootstrap NestJS application', error.stack);
    throw error;
  }
}

// For local development
if (process.env.NODE_ENV !== 'production' || (process.env.VERCEL !== '1' && !process.env.NOW_REGION)) {
  bootstrap();
}

// Default export for Vercel
export default async (req: any, res: any) => {
  try {
    const app = await bootstrap();
    return app(req, res);
  } catch (err: any) {
    console.error('SERVERLESS_FUNCTION_ERROR:', err);
    res.status(500).json({
      statusCode: 500,
      message: 'Internal Server Error',
      error: err.message,
    });
  }
};
